(1419, 'montezuma', 'a:72:{s:9:"thank_you";s:0:"";s:11:"favicon_url";s:67:"http://localhost/pun/wp-content/themes/montezuma/images/favicon.ico";s:19:"meta_xua_compatible";s:62:"<meta http-equiv="X-UA-Compatible" content="ie=edge;chrome=1">";s:13:"meta_viewport";s:51:"<meta name="viewport" content="width=device-width">";s:12:"wp_generator";i:1;s:8:"xfn_link";i:1;s:16:"wlwmanifest_link";i:1;s:8:"rsd_link";i:1;s:10:"feed_links";i:1;s:16:"feed_links_extra";i:1;s:31:"adjacent_posts_rel_link_wp_head";i:1;s:15:"insert_head_top";s:0:"";s:18:"insert_head_bottom";s:0:"";s:21:"comments_title_single";s:32:"<span>One</span> thought on %2$s";s:21:"comments_title_plural";s:34:"<span>%1$s</span> thoughts on %2$s";s:11:"avatar_size";i:50;s:17:"avatar_size_small";i:35;s:10:"avatar_url";s:0:"";s:17:"comment_quicktags";s:31:"strong,em,link,block,code,close";s:20:"comment_allowed_tags";a:12:{i:0;s:1:"a";i:1;s:4:"abbr";i:2;s:7:"acronym";i:3;s:1:"b";i:4;s:10:"blockquote";i:5;s:4:"cite";i:6;s:4:"code";i:7;s:3:"del";i:8;s:2:"em";i:9;s:1:"q";i:10;s:6:"strike";i:11;s:6:"strong";}s:20:"comment_notes_before";s:0:"";s:19:"comment_notes_after";s:0:"";s:11:"editing-css";s:0:"";s:15:"choose_css_grid";s:14:"resp12-px-m0px";s:18:"color_picker_solid";s:0:"";s:24:"color_picker_transparent";s:0:"";s:12:"google_fonts";s:25:"Yanone+Kaffeesatz:400,200";s:11:"about-menus";s:0:"";s:10:"menu-icons";s:0:"";s:22:"insert_for_css_sprites";s:150:"\r\n.widget ul li\r\n.widget h3\r\n.breadcrumbs ol li\r\n.hentry ul li\r\n.comment-text ul li\r\nli.has-sub-menu a\r\n.menu > li > a\r\n.post-tags\r\n.post-categories\r\n";s:17:"dual-title-colors";s:111:"\r\n#sitetitle a\r\n.hentry h2 a[rel=bookmark]\r\n.hentry h1 a[rel=bookmark]\r\n.image-attachment h1\r\n.widget h3 span\r\n";s:11:"css_content";s:8641:"/********************************************************************\r\n	BORDER-BOX\r\n	Browser-support: IE8+\r\n	See also http://paulirish.com/2012/box-sizing-border-box-ftw/\r\n********************************************************************/\r\n\r\n* { \r\n	-moz-box-sizing: 		border-box; \r\n	-webkit-box-sizing: 	border-box; \r\n	box-sizing: 			border-box;	\r\n	o-box-sizing: 			border-box;\r\n}\r\n\r\n.row, .lw { \r\n	-moz-box-sizing: 		content-box; \r\n	-webkit-box-sizing: 	content-box; \r\n	box-sizing: 			content-box;	\r\n	o-box-sizing: 			content-box;\r\n}\r\n\r\n\r\n/********************************************************************\r\n	<BODY> for TEXT\r\n********************************************************************/\r\n\r\nbody {\r\n	margin: 					0; \r\n	padding: 				0; \r\n	font-family: 			"Segoe UI", "Lucida Grande", "Helvetica Neue", sans-serif;\r\n	font-size: 				15px;\r\n	opacity: 				0; /* <--- will be turned on ( = set to ''1'' ) with jQuery \r\n									after google fonts, if any, have loaded and masonry layout, \r\n									if any, was applied */\r\n}\r\n\r\n\r\n/********************************************************************\r\n	HTML5 tags for IE8 and older\r\n********************************************************************/\r\n\r\narticle, aside, details, \r\nfigcaption, figure, footer, \r\nheader, hgroup, menu, \r\nnav, section { \r\n	display: 				block; \r\n}\r\n\r\n\r\n/********************************************************************\r\n	<A> LINKS\r\n********************************************************************/\r\n\r\na  { \r\n	color: 					#0090d3; \r\n	text-decoration: 		none; \r\n}\r\n\r\na:hover 	{ \r\n	text-decoration: 		underline; \r\n}\r\n\r\na:active { }\r\na:visited { }\r\n\r\n/* These get a fat dotted underline on hover */\r\n.post-bodycopy a,\r\n.widget a,\r\n.singlenav a,\r\n.commentlist a,\r\n.logged-in-as a  { \r\n	border-bottom: 		dotted 2px transparent; /* "invisible" placeholder for link hover color */\r\n}\r\n\r\n.post-bodycopy a:hover,\r\n.widget a:hover,\r\n.singlenav a:hover,\r\n.commentlist a:hover,\r\n.logged-in-as a:hover {\r\n	border-bottom-color: #0090d3;\r\n	text-decoration: 		none;\r\n	color: 					#0090d3;	\r\n}\r\n\r\n\r\n/********************************************************************\r\n	Animation\r\n	Add all CSS selectors that should be animated on hover\r\n********************************************************************/\r\n\r\na,\r\n.widget > h3, \r\n.widget > h3 i,\r\n.ed_button,\r\n#submit,\r\n.thumb-shadow,\r\n.firstpart,\r\n#menu1 i,\r\n.commentlist li > div,\r\n.commentlist .avatar {\r\n	transition: 			all 0.5s ease-out;\r\n	-webkit-transition: 	all 0.5s ease-out;\r\n	-moz-transition: 		all 0.5s ease-out;\r\n	-o-transition: 		all 0.5s ease-out;\r\n}	\r\n\r\n\r\n/********************************************************************\r\n	<H1> - <H6> HEADINGS\r\n********************************************************************/\r\n\r\nh1, h2, h3, h4, h5, h6 {\r\n	font-family: 			''Yanone Kaffeesatz'', sans-serif;\r\n	font-weight: 			normal; \r\n	margin: 					20px 0 10px 0;\r\n}\r\n\r\nh1 { font-size: 40px; line-height: 44px; }\r\nh2 { font-size: 36px; line-height: 40px; }\r\nh3 { font-size: 32px; line-height: 35px; }\r\nh4 { font-size: 28px; line-height: 33px; }\r\nh5 { font-size: 24px; line-height: 27px; }\r\nh6 { font-size: 20px; line-height: 22px; }\r\n\r\n\r\n/********************************************************************\r\n	<IMG> IMAGES\r\n********************************************************************/\r\n\r\na img { border: none; }\r\n\r\n.wp-caption {\r\n}\r\n\r\n.wp-caption.alignleft,\r\n.wp-caption.alignnone {\r\n	margin-left: 			-9px;\r\n}\r\n\r\n.wp-caption.alignright {\r\n	margin-right: 			-9px;\r\n}\r\n\r\n.wp-caption img {\r\n	background: 			#fff; \r\n	padding: 				10px; \r\n	border: 					solid 2px #cccccc;\r\n}\r\n\r\n.wp-caption-text 	{ \r\n	color:					#999999;\r\n	font-size: 				14px; \r\n	text-align: 			center; \r\n	padding: 				5px; \r\n	margin-bottom:			0; /* this is a p element, turn off its bottom margin */\r\n}\r\n\r\niframe .player-container {\r\n	border: 					solid 3px #000000;\r\n}\r\n\r\n.alignleft { \r\n	float: 					left; \r\n	margin: 					5px 15px 10px 0; \r\n}\r\n\r\n.alignright { \r\n	float: 					right; \r\n	margin: 					5px 0 10px 15px; \r\n}\r\n\r\n.aligncenter { \r\n	display: 				block; \r\n	margin: 					0 auto; \r\n}\r\n\r\n.gallery-caption {\r\n}\r\n\r\n/* images inside posts on singular pages */\r\nbody.page .hentry img,\r\nbody.single .hentry img {\r\n	padding: 				7px;\r\n	border: 					solid 1px #dddddd;\r\n}\r\n\r\n\r\n/********************************************************************\r\n Resizing content\r\n********************************************************************/\r\n\r\n.widget, .hentry {\r\n	word-wrap: 				break-word;\r\n}\r\n\r\ninput, \r\ntextarea {\r\n	max-width: 				100%;\r\n}\r\n\r\nimg { \r\n	max-width: 				100%; \r\n	height: 					auto; \r\n	display: 				block;\r\n	width: 					auto; /* makes IE8 keep aspect ratio, adjusts the height */\r\n}\r\n\r\nimg.wp-smiley {\r\n	display: 				inline; /* reset display:block for smileys */\r\n}\r\n\r\n.wp-caption {\r\n	max-width: 				100% !important; 	/* WP adds ''width="..."'' inline to images with .wp-caption, and \r\n															to embedded videos, adding !important to overwrite that */\r\n	width: 					auto; /* makes IE8 keep aspect ratio, adjusts the height */\r\n	height: 					auto;\r\n}\r\n\r\nembed {\r\n	max-width: 				100% !important; 	/* WP adds ''width="..."'' inline to images with .wp-caption, \r\n															and to embedded videos, adding !important to overwrite that */\r\n	border: 					none; \r\n	height: 					auto;\r\n}\r\n\r\n\r\n\r\n/********************************************************************\r\n	<UL> UNORDERED LISTS in post and comment bodies\r\n********************************************************************/\r\n\r\n.hentry ul,\r\n.comment-text ul {\r\n	list-style: 			none; \r\n	margin: 					10px 0;\r\n	padding-left:			25px;\r\n}\r\n\r\n.hentry ul li,\r\n.comment-text ul li {\r\n	padding-left: 			0px;\r\n	margin-bottom: 		2px;\r\n}\r\n\r\n.hentry ul li i,\r\n.comment-text ul li i {\r\n	display: 				inline-block;\r\n	width: 					12px; \r\n	height: 					12px; \r\n	margin-right: 			10px; \r\n	margin-left: 			-20px;\r\n	background: 			transparent url(%tpldir%/images/icons.png) -48px -84px no-repeat;\r\n}\r\n\r\n.hentry ul ul li i,\r\n.comment-text ul ul li i {\r\n	background: 			transparent url(%tpldir%/images/icons.png) 0px -48px no-repeat;\r\n}\r\n\r\n.hentry ul ul,\r\n.comment-text ul ul {\r\n	margin-top: 			2px; \r\n	margin-bottom: 		3px; \r\n	margin-left: 			0px;\r\n	padding-left:			22px;\r\n}\r\n\r\n\r\n\r\n/********************************************************************\r\n	<OL> ORDERED LISTS in post and comment bodies\r\n********************************************************************/\r\n\r\n.hentry ol,\r\n.comment-text ol { \r\n	margin: 					0; \r\n	padding: 				0 0 0 35px; \r\n	list-style: 			decimal;\r\n}\r\n\r\n.hentry ol li,\r\n.comment-text ol li { \r\n	margin-bottom: 		5px; \r\n}\r\n\r\n.hentry ol ol,\r\n.comment-text ol ol { \r\n	list-style: 			lower-roman; \r\n}\r\n\r\n.hentry ol ol ol,\r\n.comment-text ol ol ol { \r\n	list-style: 			lower-alpha; \r\n}\r\n\r\n\r\n/********************************************************************\r\n	<DL> Definition Lists in post and comment bodies\r\n********************************************************************/\r\n\r\n.hentry dl,\r\n.comment-text dl { \r\n	margin:					20px 0;\r\n}\r\n\r\n.hentry dt,\r\n.comment-text dt { \r\n	font-weight: 			bold; \r\n	margin:					10px 0;\r\n}\r\n\r\n.hentry dd,\r\n.comment-text dd { \r\n	margin-left:			0;\r\n	padding-left:			20px;\r\n}\r\n\r\n\r\n/********************************************************************\r\n	<TABLE> in post and comment bodies\r\n********************************************************************/\r\n\r\n.hentry table,\r\n.comment-text table {\r\n	border-collapse:		collapse;\r\n	border-bottom:			solid 1px #e7e7e7;\r\n	margin-bottom:			10px;\r\n}\r\n\r\n.hentry  table caption,\r\n.comment-text table caption {\r\n	color: 					#707070; \r\n	font-size: 				14px; \r\n	letter-spacing: 		1px;\r\n	text-transform: 		uppercase; \r\n	padding: 				4px;\r\n}\r\n\r\n.hentry  table th,\r\n.comment-text table th {\r\n	padding: 				6px 8px 6px 9px; \r\n	text-align: 			left;\r\n}\r\n\r\n.hentry  table td,\r\n.comment-text table td {\r\n	padding: 				5px 8px;\r\n	border-top:				solid 1px #e7e7e7;\r\n}\r\n\r\n/* zebra-stripe even table rows. Class ''alternate'' added with jQuery */\r\n.hentry  table tr.alternate td,\r\n.comment-text table tr.alternate td {\r\n	background: 			#f7f7f7;\r\n}\r\n\r\n\r\n/********************************************************************\r\n	<BLOCKQUOTE>\r\n********************************************************************/\r\n\r\nblockquote {\r\n	color: 					#777777;\r\n	border-left: 			5px solid #dddddd;\r\n	margin: 					15px 30px 0px 10px;\r\n	padding-left: 			20px;\r\n}\r\n\r\nblockquote p {\r\n\r\n}\r\n\r\n/********************************************************************\r\n	<PRE>\r\n********************************************************************/\r\n\r\npre {\r\n	padding:				10px;\r\n	border:				dashed 1px #cccccc; \r\n}\r\n\r\n\r\n";s:10:"css_layout";s:1112:"/* Uncomment for boxed style,\r\nalso uncomment div#container in main templates */\r\n/*\r\n#container {\r\n	display: table;\r\n	-moz-box-sizing: content-box; \r\n	-webkit-box-sizing: content-box; \r\n	box-sizing: content-box;	\r\n	-o-box-sizing: content-box;	\r\n	margin: 0 auto;\r\n	box-shadow: 0 0 30px -20px #999;\r\n	border: solid 1px #ddd;\r\n}\r\n*/\r\n\r\n/* The header area full width background */\r\n#banner-bg 	{ \r\n}\r\n\r\n/* The header area */\r\n#banner {\r\n	position: 				relative;\r\n}\r\n\r\n#logo-area {\r\n	margin-left: 			20px;\r\n}\r\n\r\n@media only screen and (max-width: 959px) {\r\n	#banner > div, #banner > nav {\r\n		float: none;\r\n		width: 100%;\r\n	}\r\n}\r\n\r\n\r\n/* The main row, with the #content column and perhaps sidebars left/right */\r\n#main 	{ \r\n	padding: 				30px 0;\r\n}\r\n\r\n/* A sidebar */\r\n#widgetarea-one {  \r\n	padding: 				0 20px;\r\n}	\r\n\r\n/* The content column */\r\n#content{ \r\n	padding: 				0 20px;\r\n}\r\n\r\n/* The footer full width background */\r\n#footer-bg { \r\n	background: 			#fcfcfc; \r\n	padding: 				30px 0; \r\n	border-top: 			solid 1px #ddd;\r\n}\r\n\r\n/* Footer content area */\r\n#footer { \r\n	text-align: 			center;\r\n	color:					#666666;\r\n}\r\n";s:9:"css_posts";s:6964:"/*****************************************\r\nPOST Container, contains all post parts\r\n******************************************\r\n\r\n/* all post containers = .hentry elements */\r\n.hentry {\r\n	position: 				relative; /* <-- This relative so that child elements can be positioned absolutely */\r\n	padding: 				30px 0 25px 0;\r\n	border-top: 			solid 1px #eee;\r\n}\r\n\r\n/* First post container */\r\n.hentry:first-child { \r\n	border-top: 			none; \r\n}\r\n\r\n/* All links inside post container .hentry */\r\n.hentry a { \r\n	color: 					#0090d3; \r\n	text-decoration: 		none; \r\n}\r\n\r\n\r\n/*****************************************\r\nPOST HEADLINES\r\n<div class="hentry"><h2><span class="post-format"></span>\r\n<a><span class="firstpart">Post</span> Title</a></h2></div>\r\n******************************************/\r\n\r\n.hentry > h1, \r\n.hentry > h2 { \r\n	margin: 					0; \r\n	font-weight: 			normal; \r\n}\r\n\r\n.hentry > h2 { \r\n	font-size: 				40px; \r\n	line-height: 			38px; \r\n	margin: 					0 0 15px 0; \r\n}\r\n\r\n/* On single post pages and static pages: */\r\n.hentry > h1 { \r\n	color: 					#0090d3;\r\n	font-size: 				50px; \r\n	line-height: 			45px; \r\n	margin: 					0 0 20px 0; \r\n}\r\n\r\n.hentry h2 a .firstpart,\r\n.hentry h1 .firstpart  { \r\n	color: 					#000000; \r\n	text-decoration: 		none; \r\n}\r\n\r\n/* change on .hentry hover */\r\n.hentry:hover h2 a .firstpart {\r\n	color: 					#0090d3;\r\n}\r\n\r\n.post-format {\r\n	display: 				none;\r\n	background: 			url(%tpldir%/images/icons.png) -120px -144px no-repeat;\r\n	width: 					24px; \r\n	height: 					24px;\r\n	padding: 				5px 13px;\r\n	margin: 					0 10px 0 0; \r\n	vertical-align: 		8px;\r\n	font-size: 				10px; \r\n\r\n}\r\n\r\n\r\n/*****************************************\r\nComment Bubble\r\n******************************************/ \r\n\r\n.hentry .comment-bubble {\r\n	display: 				inline-block;\r\n	height: 					36px; \r\n	width: 					36px;\r\n	color: 					#999; \r\n	line-height: 			27px; \r\n	font-family: 			arial, sans-serif;\r\n	font-size: 				18px;\r\n	font-weight: 			bold; \r\n	text-align: 			center; \r\n	background: 			url(%tpldir%/images/icons.png) -168px -108px no-repeat;\r\n	margin-left: 			8px; \r\n	vertical-align: 		8px;\r\n}\r\n\r\n/* change on .hentry hover */\r\n.hentry:hover .comment-bubble {\r\n	color: 					#fff; \r\n	background: 			url(%tpldir%/images/icons.png) -168px -72px no-repeat;\r\n	text-decoration: 		none;\r\n}\r\n\r\n\r\n/*****************************************\r\nPOST THUMBNAIL\r\n<div class="thumb-shadow"><div class="post-thumb"><a><img><span></span></a></div></div>\r\n******************************************/\r\n\r\n.thumb-shadow { \r\n	background: 			#fff url(%tpldir%/images/shadow.png) center 50px no-repeat;	\r\n	padding-bottom: 		5px;\r\n	margin-top: 			7px;\r\n	margin-left: 			-6px;\r\n}\r\n\r\n.hentry:hover .thumb-shadow { \r\n	background-position: center bottom;\r\n}\r\n\r\n.post-thumb { \r\n	margin: 					0 auto 15px auto; \r\n	display: 				block; \r\n	padding: 				6px 6px 5px 6px;\r\n	border: 					solid 1px #ddd;\r\n	border-bottom: 		solid 1px #ccc;\r\n	background: 			#ffffff;\r\n}\r\n\r\n.post-thumb a { \r\n	position: 				relative;\r\n	display: 				block;\r\n}\r\n\r\n/* .img_wrapper added  & wrapped around <img> by Javascript */\r\n.post-thumb .img_wrapper{\r\n}\r\n\r\n.post-thumb img {\r\n	-webkit-filter: 		grayscale(0);\r\n	opacity: 				0; \r\n	filter: 					alpha(opacity=0); \r\n	-ms-filter: 			"progid:DXImageTransform.Microsoft.Alpha(Opacity=50)";\r\n	display:					block;\r\n}\r\n\r\n.post-thumb img.img_grayscale { \r\n	filter: 					grayscale(100%); /* Current draft standard */\r\n	-ms-filter: 			grayscale(100%); \r\n	-o-filter: 				grayscale(100%); /* opera */ \r\n	filter: 					url("%tpldir%/images/gray-filter.svg#grayscale"); /* Firefox */\r\n	filter: 					gray; /* IE */\r\n	-webkit-filter: 		grayscale(1); /* Old WebKit */\r\n	-webkit-filter: 		grayscale(100%); /* New WebKit */\r\n}\r\n\r\n/* Container for the shadow. Parent is position:relative. Putting it "on top" of image */\r\n.post-thumb span {\r\n	position: 				absolute;\r\n	top: 						0; \r\n	bottom: 					0;\r\n	left: 					0; \r\n	right: 					0;\r\n	box-shadow: 			inset 15px 15px 20px -20px #000; /* inner shadow top and left */\r\n	z-index: 				20000;\r\n}\r\n\r\n\r\n/*****************************************\r\nPOST BODYCOPY\r\n******************************************\r\n<div class="post-bodycopy cf"><div class="post-date"></div><a class="comment-bubble">13</a>\r\n	<p>...</p><p>...</p>\r\n</div>\r\n******************************************/\r\n\r\n.post-bodycopy { \r\n	/* text-align: 		justify; */ \r\n}\r\n.post-bodycopy p {\r\n\r\n}\r\n\r\n\r\n/*****************************************\r\nPOST PAGINATION\r\n******************************************/\r\n/*\r\n<div class="post-pagination"><span>Page</span> <a>1</a> 2 <a>3</a></div>\r\n*/\r\n\r\n/* Box that contains the pagination links */\r\np.post-pagination {\r\n	clear: both;\r\n	margin: 20px 0;\r\n}\r\np.post-pagination ol {\r\n	margin: 15px 0;\r\n}\r\np.post-pagination a { \r\n\r\n}\r\n\r\n\r\n\r\n.post-edit-link {\r\n	display: 				block;\r\n	margin-top: 			10px;\r\n}\r\n\r\n\r\n/*****************************************\r\nPOST DATE\r\n******************************************\r\n<div class="post-date">\r\n	<p class="post-month">Jul</p>\r\n	<p class="post-day">19</p>\r\n	<p class="post-year">2013</p>\r\n</div>\r\n******************************************/\r\n\r\n.post-date {\r\n	border-right: 			solid 1px #999; \r\n	float: 					left;\r\n	text-align: 			center;\r\n	margin-right: 			10px; \r\n	margin-top: 			4px;\r\n	margin-bottom: 		8px;\r\n	padding-right: 		10px; \r\n}\r\n\r\n.post-day { \r\n	font-size: 				20px; \r\n	line-height: 			20px; \r\n	margin: 					4px auto 0px auto; \r\n}\r\n\r\n.post-month { \r\n	font-size: 				14px; \r\n	line-height: 			14px; \r\n	margin:					0px auto; \r\n	text-transform: 		uppercase; \r\n}\r\n.post-year { \r\n	font-size: 11px; \r\n	line-height: 14px; \r\n	margin: 0px auto; \r\n}\r\n\r\n\r\n/*****************************************\r\nPOST FOOTER\r\n******************************************\r\n<div class="post-footer">\r\n	<p class="post-categories"><i></i><a>Category Title</a></p>\r\n	<p class="post-tags"><i></i><a>Tag 1</a> <a>Tag 1</a></p>\r\n</div>\r\n ******************************************/\r\n \r\n.post-footer {\r\n	color: 					#bbbbbb;\r\n	margin-top: 			10px;\r\n}\r\n\r\n.post-footer a {\r\n	color: 					#aaaaaa;\r\n	padding: 				2px 10px 4px 10px;\r\n	border-radius: 		3px;\r\n	border: 					none;\r\n}\r\n\r\n.hentry:hover .post-footer a {\r\n	color: 					#0090d3;\r\n}\r\n\r\n.hentry:hover .post-footer a:hover {\r\n	text-decoration: 		none;\r\n	color: 					#ffffff;\r\n	background: 			#0090d3;\r\n}\r\n\r\n/* Post tags and categories: */\r\n.post-tags, \r\n.post-categories  { \r\n	margin: 					0; /* Turn off default margins of <p> paragraph element */\r\n	display: 				inline-block;\r\n}\r\n\r\n.post-tags  { \r\n	margin-left: 			20px;\r\n\r\n}\r\n\r\n/* This <i></i> element is inserted by Javascript, as container for icons */\r\n.post-tags i, \r\n.post-categories i {\r\n	display: 				inline-block; \r\n	width: 					12px; \r\n	height: 					12px;\r\n	margin-right: 			5px;\r\n	background-image: 	url(%tpldir%/images/icons.png);\r\n}\r\n\r\n.post-tags i { \r\n	background-position: -168px -192px; \r\n}\r\n\r\n.post-categories i { \r\n	background-position: -12px -228px; \r\n}\r\n\r\n.post-readmore { \r\n	float: 					right;\r\n	margin-top: 			-5px;\r\n}\r\n\r\n";s:11:"css_gallery";s:6090:"/********************************************************************\r\n	Galleries (inserted with the WP [gallery] shortcode:\r\n	The (invalid as per W3C) HTML code pf [gallery] is changed,\r\n	dl/dt/dd replaced with figure/figcaption, and the (invalid because inside a DIV) inline CSS \r\n	replaced with the styling here:\r\n********************************************************************/\r\n\r\n.gallery {\r\n	margin:					20px 0 10px -7px; /* visual adjustment related to padding/border of figure element */ \r\n}\r\n\r\n.gallery figure {\r\n	display:					inline-block;\r\n	padding: 				7px;\r\n	border:					solid 1px #ccc;\r\n	margin:					0 10px 5px 0; /* reset default figure margin */\r\n	box-shadow: 			0 0 10px -5px #ccc;\r\n}\r\n\r\n.gallery figure a {\r\n	position:				relative; /* to position child span (shadow) absolute */\r\n	display: 				block;\r\n	border:					0;\r\n	text-decoration:		none;\r\n}\r\n\r\n/* Container for the shadow. Parent is position:relative. Putting it "on top" of image */\r\n.gallery figure a span {\r\n	position: 				absolute;\r\n	top: 						0; \r\n	bottom: 					0;\r\n	left: 					0; \r\n	right: 					0;\r\n	box-shadow: 			inset 15px 15px 20px -20px #000; /* inner shadow top and left */\r\n	z-index: 				1000;\r\n}\r\n.ie8 .gallery figure a span {\r\n	background:				#fff;\r\n	-ms-filter:				"progid:DXImageTransform.Microsoft.Alpha(Opacity=0)"; /* first! */\r\n	filter: 					alpha(opacity=0);\r\n}\r\n.ie8 .gallery figure {\r\n	margin:					0 10px 10px 0; /* +5px more margin-bottom for IE8 */\r\n}\r\n\r\n\r\n/* adding .hentry to this selector for more "specificity" to be able to overwrite inherited \r\nstrong rules "body.single .hentry img" and "body.page .hentry img": */\r\n.hentry .gallery figure a img {\r\n	border:					0; /* reset possibly inherited border from ".hentry img" */\r\n	padding:					0; /* reset possibly inherited border from ".hentry img" */\r\n}\r\n\r\n/* captions currently not displayed on gallery page, only on single image pages */\r\n.gallery figcaption {\r\n}\r\n\r\n\r\n/********************************************************************\r\n	For single images on image.php:\r\n********************************************************************/\r\n.image-attachment .post-bodycopy img {\r\n	margin-left: -8px;\r\n}\r\n.image-attachment .singlenav {\r\n	margin: 10px 0;\r\n}\r\n.image-attachment .singlenav .older a:before {\r\n	content: ''\\2039\\2039 \\00a0''; /* This is ISO numeric code for &larr;. For more see http://www.evotech.net/blog/2007/04/named-html-entities-in-numeric-order/ */ \r\n}\r\n.image-attachment .singlenav .newer a:after {\r\n	content: ''\\00a0 \\203a\\203a''; /* &rarr; */\r\n}\r\n\r\n\r\n/********************************************************************\r\n	For the colorbox overlays:\r\n********************************************************************/\r\n/* This section should not be changed */\r\n#colorbox, #cboxOverlay, #cboxWrapper{position:absolute; top:0; left:0; z-index:9999; overflow:hidden;}\r\n#cboxOverlay{position:fixed; width:100%; height:100%;}\r\n#cboxMiddleLeft, #cboxBottomLeft{clear:left;}\r\n#cboxContent{position:relative;}\r\n#cboxLoadedContent{overflow:auto;}\r\n#cboxTitle{margin:0;}\r\n#cboxLoadingOverlay, #cboxLoadingGraphic{position:absolute; top:0; left:0; width:100%; height:100%;}\r\n#cboxPrevious, #cboxNext, #cboxClose, #cboxSlideshow{cursor:pointer;}\r\n.cboxPhoto{float:left; margin:auto; border:0; display:block; max-width:none;}\r\n.cboxIframe{width:100%; height:100%; display:block; border:0;}\r\n#colorbox, #cboxContent, #cboxLoadedContent{\r\n	-moz-box-sizing:content-box; -webkit-box-sizing:content-box; \r\n	box-sizing:content-box; o-box-sizing:content-box;\r\n}\r\n\r\n/* \r\n    User Style:\r\n    Change the following styles to modify the appearance of ColorBox.  They are\r\n    ordered & tabbed in a way that represents the nesting of the generated HTML.\r\n*/\r\n#cboxOverlay{background:#fff;}\r\n#colorbox{}\r\n	\r\n    #cboxTopLeft{width:25px; height:25px; background:url(%tpldir%/images/colorbox/border1.png) no-repeat 0 0;}\r\n    #cboxTopCenter{height:25px; background:url(%tpldir%/images/colorbox/border1.png) repeat-x 0 -50px;}\r\n    #cboxTopRight{width:25px; height:25px; background:url(%tpldir%/images/colorbox/border1.png) no-repeat -25px 0;}\r\n    #cboxBottomLeft{width:25px; height:25px; background:url(%tpldir%/images/colorbox/border1.png) no-repeat 0 -25px;}\r\n    #cboxBottomCenter{height:25px; background:url(%tpldir%/images/colorbox/border1.png) repeat-x 0 -75px;}\r\n    #cboxBottomRight{width:25px; height:25px; background:url(%tpldir%/images/colorbox/border1.png) no-repeat -25px -25px;}\r\n    #cboxMiddleLeft{width:25px; background:url(%tpldir%/images/colorbox/border2.png) repeat-y 0 0;}\r\n    #cboxMiddleRight{width:25px; background:url(%tpldir%/images/colorbox/border2.png) repeat-y -25px 0;}\r\n	 \r\n    #cboxContent{background:#fff; overflow:hidden;}\r\n        .cboxIframe{background:#fff;}\r\n        #cboxError{padding:50px; border:1px solid #ccc;}\r\n        #cboxLoadedContent{margin-bottom:20px;}\r\n        #cboxTitle{position:absolute; bottom:0px; left:0; text-align:center; width:100%; color:#999;}\r\n        #cboxCurrent{position:absolute; bottom:0px; left:100px; color:#999;}\r\n        #cboxSlideshow{position:absolute; bottom:0px; right:42px; color:#444;}\r\n        #cboxPrevious{position:absolute; bottom:0px; left:0; color:#444;}\r\n        #cboxNext{position:absolute; bottom:0px; left:63px; color:#444;}\r\n        #cboxLoadingOverlay{background:#fff url(%tpldir%/images/colorbox/loading.gif) no-repeat 5px 5px;}\r\n        #cboxClose{position:absolute; bottom:0; right:0; display:block; color:#444;}\r\n\r\n/*\r\n  The following fixes a problem where IE7 and IE8 replace a PNG''s alpha transparency with a black fill\r\n  when an alpha filter (opacity change) is set on the element or ancestor element.  This style is not applied to or needed in IE9.\r\n  See: http://jacklmoore.com/notes/ie-transparency-problems/\r\n*/\r\n.cboxIE #cboxTopLeft,\r\n.cboxIE #cboxTopCenter,\r\n.cboxIE #cboxTopRight,\r\n.cboxIE #cboxBottomLeft,\r\n.cboxIE #cboxBottomCenter,\r\n.cboxIE #cboxBottomRight,\r\n.cboxIE #cboxMiddleLeft,\r\n.cboxIE #cboxMiddleRight {\r\n    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr=#00FFFFFF,endColorstr=#00FFFFFF);\r\n}\r\n\r\n";s:11:"css_widgets";s:5328:"/********************************************************************\r\n	WIDGET CONTAINER\r\n	<aside id="categories-3" class="widget widget_categories cf">\r\n********************************************************************/\r\n\r\n/* Widget container, contains all parts of a widget */\r\n.widget {\r\n	font-size: 14px;\r\n	line-height: 		1.3;\r\n	color: 				#666666;\r\n	margin-bottom: 	30px;\r\n}\r\n\r\n/* Widget container, when hovered */\r\n.widget:hover { \r\n  	color: 				#000000;\r\n}\r\n\r\n\r\n/********************************************************************\r\n	Links inside Widgets\r\n********************************************************************/\r\n\r\n/* All links inside widgets */\r\n.widget a { \r\n  	color: 				#000000;\r\n}\r\n\r\n/* Links inside widgets, when parent .widget is hovered */\r\n.widget:hover a { \r\n  	color: 				#0090d3;\r\n}\r\n\r\n\r\n/********************************************************************\r\n	WIDGET TITLES\r\n	<h3><i></i><span><span class="firstpart">Widget</span> Title</span></h3>\r\n********************************************************************/\r\n\r\n/* Widget title h3 container, contains all parts of widget title */\r\n.widget > h3 {\r\n	color: 				#0090d3;\r\n	margin: 				0 0 15px 0; /* turning off default h3 margins, leaving only margin-bottom */\r\n	font-size:			32px;\r\n	line-height: 		29px;\r\n}\r\n\r\n/* Widget title h3 container, when parent .widget is hovered */\r\n.widget:hover > h3 { \r\n  	color: 				#000000;\r\n}\r\n\r\n/* Widget title text */\r\n.widget > h3 > span {\r\n}\r\n\r\n/* First part of widget title text */\r\n.widget h3 span .firstpart {\r\n	color: 				#000000;\r\n	text-decoration: 	none;\r\n}\r\n\r\n/* Global widget title icon */\r\n.widget > h3 i {\r\n	display: 			inline-block;\r\n	width: 				24px;\r\n	height: 				24px;\r\n	margin-right: 		15px;\r\n	background-color: #ffffff;\r\n	background-image: url( %tpldir%/images/icons.png );\r\n}\r\n\r\n/* Individual widget title icons */ \r\n.widget > h3 i 						{ background-position: -120px -216px }\r\n.widget_archive > h3 i 				{ background-position: -120px -24px }\r\n.widget_tag_cloud > h3 i 			{ background-position: -120px 0px }\r\n.widget_recent_comments > h3 i 	{ background-position: -120px -72px }\r\n.widget_links > h3 i				{ background-position: -120px -96px }\r\n.widget_pages > h3 i 				{ background-position: -120px -120px }\r\n.widget_recent_entries > h3 i 	{ background-position: -120px -144px }\r\n.widget_meta > h3 i 					{ background-position: -120px -192px }\r\n.widget_categories > h3 i 			{ background-position: -120px -168px }\r\n\r\n/* Individual widget title icons, when the parent .widget is hovered */ \r\n.widget:hover > h3 i 						{ background-position: -96px -216px }\r\n.widget_archive:hover > h3 i 				{ background-position:  -96px -24px }\r\n.widget_tag_cloud:hover > h3 i 			{ background-position:  -96px 0px }\r\n.widget_recent_comments:hover > h3 i 	{ background-position:  -96px -72px }\r\n.widget_links:hover > h3 i 				{ background-position:  -96px -96px }\r\n.widget_pages:hover > h3 i 				{ background-position:  -96px -120px }\r\n.widget_recent_entries:hover > h3 i 	{ background-position:  -96px -144px }\r\n.widget_meta:hover > h3 i 					{ background-position:  -96px -192px }\r\n.widget_categories:hover > h3 i 			{ background-position:  -96px -168px }\r\n\r\n\r\n/********************************************************************\r\n	"UL" Unordered lists inside WIDGETS